mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RN, T.* FROM ( SELECT P.PURCHASE_NUM, P.PURCHASE_DATE, P.PURCHASE_TOTAL, P.PURCHASE_STATUS, P.PAYMENT_METHOD, P.BANK_NAME, P.DEPOSITOR_NAME, P.CARD_COMPANY, P.CARD_NUMBER, PL.GOODS_NUM, PL.PURCHASE_QTY, PL.PURCHASE_PRICE, G.GOODS_NAME, G.GOODS_MAIN_STORE_IMAGE, G.GOODS_CONTENTS, D.DELIVERY_COMPANY, D.TRACKING_NUMBER, /* [추가] REVIEWS 테이블과 JOIN하여 리뷰 작성 여부(1 또는 0)를 확인 */ CASE WHEN R.REVIEW_NUM IS NOT NULL THEN 1 ELSE 0 END AS REVIEW_WRITTEN FROM PURCHASE P JOIN PURCHASE_LIST PL ON P.PURCHASE_NUM = PL.PURCHASE_NUM JOIN GOODS G ON PL.GOODS_NUM = G.GOODS_NUM LEFT JOIN DELIVERY D ON P.PURCHASE_NUM = D.PURCHASE_NUM /* [추가] REVIEWS 테이블을 LEFT JOIN하여 각 주문 상품별 리뷰 존재 여부 확인 */ LEFT JOIN REVIEWS R ON P.PURCHASE_NUM = R.PURCHASE_NUM AND PL.GOODS_NUM = R.GOODS_NUM WHERE P.MEMBER_NUM = #{memberNum}
AND G.GOODS_NAME LIKE '%' || #{searchWord} || '%'
ORDER BY P.PURCHASE_DATE DESC ) T ) WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT * FROM ( SELECT ROWNUM AS RN, T.* FROM ( SELECT P.PURCHASE_NUM, P.PURCHASE_DATE, P.PURCHASE_TOTAL, P.PURCHASE_STATUS, P.MEMBER_NUM, M.MEMBER_ID, M.MEMBER_NAME FROM PURCHASE P JOIN MEMBERS M ON P.MEMBER_NUM = M.MEMBER_NUM
AND P.PURCHASE_STATUS = #{status}
AND ( P.PURCHASE_NUM LIKE '%' || #{searchWord} || '%' OR M.MEMBER_ID LIKE '%' || #{searchWord} || '%' OR M.MEMBER_NAME LIKE '%' || #{searchWord} || '%' )
ORDER BY P.PURCHASE_DATE DESC ) T ) WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT COUNT(*) FROM PURCHASE P JOIN MEMBERS M ON P.MEMBER_NUM = M.MEMBER_NUM
AND P.PURCHASE_STATUS = #{status}
AND ( P.PURCHASE_NUM LIKE '%' || #{searchWord} || '%' OR M.MEMBER_ID LIKE '%' || #{searchWord} || '%' OR M.MEMBER_NAME LIKE '%' || #{searchWord} || '%' )

mapper.update:
UPDATE PURCHASE
SET PURCHASE_STATUS = #{status}
WHERE PURCHASE_NUM = #{purchaseNum}

mapper.insert:
INSERT INTO DELIVERY (
    PURCHASE_NUM, DELIVERY_COMPANY, TRACKING_NUMBER, DELIVERY_START_DATE
) VALUES (
    #{purchaseNum}, #{deliveryCompany}, #{trackingNumber}, SYSTIMESTAMP
)
