mapper.select:
SELECT COUNT(*)
FROM REVIEWS
WHERE MEMBER_NUM = #{memberNum}

SELECT * FROM (
    SELECT ROWNUM AS RN, T.* FROM (
        SELECT
            R.REVIEW_NUM, R.GOODS_NUM, R.MEMBER_NUM, R.PURCHASE_NUM,
            R.REVIEW_RATING, R.REVIEW_CONTENT, R.REVIEW_IMAGE, R.REVIEW_DATE, R.REVIEW_STATUS,
            R.REVIEW_MOD_DATE, G.GOODS_NAME, M.MEMBER_ID FROM REVIEWS R
        JOIN GOODS G ON R.GOODS_NUM = G.GOODS_NUM
        JOIN MEMBERS M ON R.MEMBER_NUM = M.MEMBER_NUM WHERE R.MEMBER_NUM = #{memberNum}
        ORDER BY R.REVIEW_DATE DESC
    ) T
) WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT COUNT(*) FROM REVIEWS R JOIN GOODS G ON R.GOODS_NUM = G.GOODS_NUM JOIN MEMBERS M ON R.MEMBER_NUM = M.MEMBER_NUM
(UPPER(R.REVIEW_CONTENT) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(G.GOODS_NAME) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(M.MEMBER_ID) LIKE '%' || UPPER(#{searchWord}) || '%')
AND R.REVIEW_RATING = #{rating}
AND R.GOODS_NUM = #{goodsNum}

SELECT * FROM ( SELECT ROWNUM AS RN, T.* FROM ( SELECT R.REVIEW_NUM, R.REVIEW_RATING, R.REVIEW_CONTENT, R.REVIEW_DATE, R.REVIEW_IMAGE, R.REVIEW_STATUS, R.REVIEW_MOD_DATE, G.GOODS_NAME, M.MEMBER_ID FROM REVIEWS R JOIN GOODS G ON R.GOODS_NUM = G.GOODS_NUM JOIN MEMBERS M ON R.MEMBER_NUM = M.MEMBER_NUM
(UPPER(R.REVIEW_CONTENT) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(G.GOODS_NAME) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(M.MEMBER_ID) LIKE '%' || UPPER(#{searchWord}) || '%')
AND R.REVIEW_RATING = #{rating}
AND R.GOODS_NUM = #{goodsNum}
ORDER BY R.REVIEW_NUM DESC ) T ) WHERE RN BETWEEN #{startRow} AND #{endRow}

